import 'package:flutter/material.dart';import 'package:introduction_screens_lesson/intro_screens/intro_page_1.dart';import 'package:introduction_screens_lesson/intro_screens/intro_page_2.dart';import 'package:introduction_screens_lesson/intro_screens/intro_page_3.dart';import 'package:smooth_page_indicator/smooth_page_indicator.dart';import 'home_page.dart';class OnBoardingScreen extends StatefulWidget {  const OnBoardingScreen({Key? key}) : super(key: key);  @override  State<OnBoardingScreen> createState() => _OnBoardingScreenState();}class _OnBoardingScreenState extends State<OnBoardingScreen> {  final PageController _controller = PageController();  bool onLastPage = false;  @override  Widget build(BuildContext context) {    return Scaffold(      body: Stack(        children: [          //page view          PageView(            controller: _controller,            onPageChanged: (index) {              setState(() {                onLastPage = (index == 2);              });            },            // slider pages            children: const [              IntroPage1(),              IntroPage2(),              IntroPage3(),            ],          ),          // dot indicators          Container(            alignment: const Alignment(0, 0.75),            child: Row(              mainAxisAlignment: MainAxisAlignment.spaceEvenly,              children: [                // skip button                GestureDetector(                  child: const Text('skip'),                  onTap: () {                    _controller.jumpToPage(2);                  },                ),                // doter slider                SmoothPageIndicator(                  controller: _controller,                  count: 3,                  effect: const SwapEffect(type: SwapType.yRotation,dotColor:Colors.blueGrey ),                ),                //next or done                onLastPage                    ? GestureDetector(                        child: const Text('done'),                        onTap: () {                          Navigator.push(                            context,                            MaterialPageRoute(builder: (context) {                              return const HomePage();                            }),                          );                        },                      )                    : GestureDetector(                        child: const Text('next'),                        onTap: () {                          _controller.nextPage(                              duration: const Duration(milliseconds: 500),                              curve: Curves.easeIn);                        },                      )              ],            ),          ),        ],      ),    );  }}